#!/usr/bin/node

const express = require('express');
const indexRouter = require('./routes/index');

const app = express();
const port = process.env.PORT || 5000;

app.use('/', indexRouter);

app.listen(port, () => {
    console.log(`Server Listening on port ${port}`);
})

module.exports = app;

